void draw_PAUSE(int pos_x, int pos_y, std::string pic) {
    SetConsoleOutputCP(CP_UTF8);
    GotoXY(pos_x, pos_y);
    for (auto i : pic) {
        switch (i)
        {
        case '.':
            changeTextColor({ 253, 255, 154 });
            break;
        case ':': case'Y':
            changeTextColor({ 239, 165, 27 });
            break;
        case '=': case'R':
            changeTextColor({ 241, 85, 57 });
            break;
        case 'G':
            changeTextColor({ 52, 190, 75 });
            break;
        case 'B':
            changeTextColor({ 53, 103, 36 });
            break;
        case 'O':
            changeTextColor({ 64, 168, 172 });
            break;
        case '*':
            changeTextColor({ 48, 83, 76 });
            break;
        case 'Z':
            changeTextColor({ 245, 245, 245 });
            break;
        case '#':
            changeTextColor({ 130, 128, 125 });
            break;
        case 'H':
            changeTextColor({ 0, 0, 0 });
            break;
        default:
            changeTextColor({ 162, 91, 98 });
            break;
        }
        if (i > 32) cout << u8"\u2588";
        else cout << i;

    }
}



